<?phpclass StickersController extends BackendAppController {	var $name = 'Stickers';	var $uses = array('Backend.Sticker');	var $components = array('Session', 'Auth', 'Backend.General');	var $paginate = array();
	function index() {		// breadcrumb define		$breadcrumb['Home'] = Configure::read('AdminSiteUrl').'dashboard';		$breadcrumb['Sticker'] = 'javascript:void(0)';		$this->set('breadcrumb', $breadcrumb);		$material_data = $this->paginate('Sticker');				$this->set('material_data', $material_data);	}
	function create() {		// breadcrumb define		$breadcrumb['Home'] = Configure::read('AdminSiteUrl').'dashboard';		$breadcrumb['Sticker'] = Configure::read('AdminSiteUrl').'stickers/index';			$breadcrumb['Create'] = 'javascript:void(0)';		$this->set('breadcrumb', $breadcrumb);				/*Load Sticker Models*/		$this->loadModel('StickerSize');		$this->loadModel('StickerQuantity');		$this->loadModel('StickerMatrial');				if(!empty($this->data)){			$this->Sticker->set($this->data);			/*Set the image validation*/			if(empty($this->data['Sticker']['new_image'])){				$this->Sticker->validationErrors['image'][] = "Please select image";			}			/*Set the size validation*/			if(!empty($this->data['Sticker']['size'])){				foreach($this->data['Sticker']['size'] as $size){					if(empty($size['height']) || empty($size['width'])){ 						$this->Sticker->validationErrors['size'] = "Please enter size in width & height";					}				}			}						/*Set the material error*/			if(!empty($this->data['Sticker']['material'])){				foreach($this->data['Sticker']['material'] as $material){					if(empty($material)){ 						$this->Sticker->validationErrors['material'] = "Please select material";					}				}			}						/*Set the quantity validation*/			if(!empty($this->data['Sticker']['quantity'])){				foreach($this->data['Sticker']['quantity'] as $quantity){					if(empty($quantity['unit']) || empty($quantity['discount'])){ 						$this->Sticker->validationErrors['quantity'] = "Please enter quantity unit & discount";					}				}			}			$this->Sticker->setValidation('NewSticker');						if($this->Sticker->validates()){							}		}	}

};?>